import React from 'react';

function InfoCard(props) {
    const pStyle = {
        padding: '4%',
        textAlign: 'initial'
    }
    let _img = {
        backgroundImage: 'url(' + props.img + ')',
        position: 'absolute'
    }
    return (
        <div class="InfoCard_class">
            <style>{ `
            div.InfoCard_img.{
                position: 'relative';
            }
            div.InfoCard_img::before {
                content: "";
                display: block;
                padding-top: 100%; /* 比率を指定 */
            }
            `}</style>
            <a href={ props.link }>
                <div class="InfoCard_img">
                    <img style={ _img }></img>
                </div>
                <div style={ pStyle }>
                    <h3>
                        { props.title }
                    </h3>
                    <p>
                        { props.note }
                    </p>
                </div>
            </a>
        </div>
    );
};
export default InfoCard;
