import React from 'react';

function InfoCard(props) {
    const infoCardStyle = {
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center"
    }
    const imgStyle = {
        width: "33%"
    }
    return (
        <a href={ props.link }>
            <div style={ infoCardStyle }>
                <img src={ props.img } style={ imgStyle } />
                <div>
                    <h4>
                        { props.title }
                    </h4>
                    <p>
                        { props.note }
                    </p>
                </div>
            </div>
        </a>
    );
};
export default InfoCard;
