import React from 'react';

function InfoCard(props) {
    const infoCardStyle = {
        display: "flex",
        with: "100px"
    };

    return (
        <a href={ props.link }>
            <div style={ infoCardStyle }>
                <img src={ props.img } />
                <div>
                    <h4>
                        { props.title }
                    </h4>
                    <p>
                        { props.note }
                    </p>
                </div>
            </div>
        </a>
    );
};
export default InfoCard;
