import React from 'react';

function InfoCard(props) {
    const pStyle = {
        padding: '4%',
        textAlign: 'initial'
    }
    let _img = {
        backgroundImage: 'url(' + props.img + ')',
        position: 'absolute',
        justifyContent: 'center',
        alignItems: 'center'
    }
    return (
        <div class="InfoCard_class">
            <style>{ `
            div.InfoCard_img{
                -moz-box-sizing: border-box;
                box-sizing: border-box;
                background-size: cover;
                width: 100%;
                height: 500px;
                padding-top: 62.5%;
            }
            `}</style>
            <a href={ props.link }>
                <div class="InfoCard_img">
                    <img style={ _img }></img>
                </div>
                <div style={ pStyle }>
                    <h3>
                        { props.title }
                    </h3>
                    <p>
                        { props.note }
                    </p>
                </div>
            </a>
        </div>
    );
};
export default InfoCard;
