import React from 'react';

const infoCardStyle = {
    margin: "1%",
    width: "48%"
}
const imgStyle = {
    width: "100%"
}
const pStyle = {
    padding: "5%"
}
function InfoCard(props) {
    let _img = {
        backgroundImage: 'url(' + this.props.img + ')'
    }
    return (
        <div style={ infoCardStyle }>
            <a href={ props.link }>
                <div style={ _img }></div>
                <div style={ pStyle }>
                    <h4>
                        { this.props.title }
                    </h4>
                    <p>
                        { this.props.note }
                    </p>
                </div>
            </a>
        </div>
    );
};
export default InfoCard;
