import React, { Component } from 'react';

class Container extends Component {
    render() {
        return (
            <div class="container">
                <style jsx>
                    { `
                    @media only screen and (max-width:1023px) {
                        Container_child{
                            padding: 0 2%;
                        }
                        Container_child:nth-child(odd){
                            background: #f5f5f5;
                        }
                    }
                    @media only screen and (min-width:1024px) {
                        #Container {
                            max-width: 1024px;
                            margin: 0 auto;
                        }
                        Container_child:nth-child(odd){
                            background: #f5f5f5;
                        }
                    }
                    `}
                </style>
                <div class="container_child">
                    { this.props.children }
                </div>
            </div>);
    }
}
export default Container;
