import React from 'react';

function InfoCard(props) {
    const pStyle = {
        padding: '4%',
        textAlign: 'initial'
    }
    let _img = {
        backgroundImage: 'url(' + props.img + ')',
        backgroundSize: 'cover',
        // height: 'auto',
        backgroundPosition: 'center center',
        backgroundSize: '100px auto'
    }
    return (
        <div class="InfoCard_class">
            <a href={ props.link }>
                <div style={ _img }></div>
                <div style={ pStyle }>
                    <h3>
                        { props.title }
                    </h3>
                    <p>
                        { props.note }
                    </p>
                </div>
            </a>
        </div>
    );
};
export default InfoCard;
